import React, {useState} from 'react'
import {Link, useNavigate} from 'react-router-dom';
// import 'bootstrap/dist/css/bootstrap.min.css';
import '../Components/CSS/Dashboard.css'; // Ensure you have CSS file for styles


function CustomerDashboard() {

    const navigate = useNavigate();
    const handleLogout = () => {
        // Perform any necessary logout operations, e.g., clearing tokens
        localStorage.removeItem('token'); // Example: removing a token from local storage
        // Redirect to login page
        navigate('/');
      };

    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);

    // change nav color when scrolling
    const [color, setColor] = useState(false);
    const changeColor = () => {
        if(window.scrollY >= 90){
            setColor(true)
        }else{
            setColor(false)
        }
    }
    window.addEventListener('scroll', changeColor)

    // close menu on click
    const closeMenu = () => setClick(false);
  return (
