import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UpdateBookForm from './UpdateBookForm'; // Import the UpdateBookForm component
import '../Components/CSS/AdminDashboard.css'; // Ensure you have CSS file for styles

function AdminDashboard() {
  const [showBooks, setShowBooks] = useState(false);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    if (showBooks) {
      axios.get('http://localhost:8080/books') // Replace with your API URL
        .then(response => {
          setBooks(response.data);
          setLoading(false);
        })
        .catch(error => {
          setError('Error fetching books. Please try again later.');
          setLoading(false);
          console.error('Error fetching books:', error);
        });
    }
  }, [showBooks]);

  const handleUpdateBook = (book) => {
    setSelectedBook(book);
  };

  const handleCloseUpdateForm = () => {
    setSelectedBook(null);
  };

  const handleUpdateSuccess = () => {
    // Refresh the book list after successful update
    axios.get('http://localhost:8080/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error refreshing book list:', error));
    handleCloseUpdateForm();
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Example: removing a token from local storage
    window.location.href = '/'; // Redirect to the home page
  };

  return (
    <div className="admin-dashboard-container">
      <div className="sidebar">
        <h2>Admin Dashboard</h2>
        <ul>
          <li><button onClick={() => setShowBooks(!showBooks)}>{showBooks ? 'Hide Books' : 'View Books'}</button></li>
          <li><button onClick={handleLogout} className="logout-button">Logout</button></li>
        </ul>
      </div>
      <div className="content">
        {showBooks && (
          <div className="books-list">
            <h2>Books Management</h2>
            {loading && <p>Loading...</p>}
            {error && <p className="error-message">{error}</p>}
            {!loading && !error && (
              <div className="book-container">
                {books.length > 0 ? (
                  books.map((book) => (
                    <div key={book.id} className="book-card">
                      <img
                        src='https://img.freepik.com/premium-photo/landscape-open-book-with-cover-mockup-mockup_641503-157256.jpg' // Default image URL
                        alt={book.bookName}
                        className="book-image"
                      />
                      <div className="book-details">
                        <h3 className="book-title">{book.bookName}</h3>
                        <p className="book-author">by {book.aname}</p>
                        <p className="book-price">${book.price}</p>
                        <button onClick={() => handleUpdateBook(book)}>Update</button>
                        <button onClick={() => handleDeleteBook(book.id)} className="btn btn-danger btn-sm ml-2">Delete</button>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No books available.</p>
                )}
              </div>
            )}
          </div>
        )}
        {selectedBook && (
          <UpdateBookForm
            bookId={selectedBook.id}
            onClose={handleCloseUpdateForm}
            onUpdate={handleUpdateSuccess}
          />
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
