import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Components/CSS/AdminDashboard.css'; // Adjust the path as needed

function AdminDashboard() {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch books data on component mount
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:8080/book'); // Adjust the endpoint as needed
        setBooks(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch books:', err);
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleLogout = () => {
    // Perform logout logic (e.g., clearing session, tokens)
    navigate('/');
  };

  const handleDelete = async (bookId) => {
    try {
      await axios.delete(`http://localhost:8080/book/${bookId}`); // Adjust the endpoint as needed
      setBooks(books.filter(book => book.book_id !== bookId));
    } catch (err) {
      console.error('Failed to delete book:', err);
    }
  };

  const handleUpdate = (bookId) => {
    navigate(`/update-book/${bookId}`); // Navigate to the update book page
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="admin-dashboard-container">
      <h2>Admin Dashboard</h2>
      <button onClick={handleLogout} className="btn btn-primary">Logout</button>
      <div className="books-section">
        <h3>Books</h3>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Book Name</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {books.map((book) => (
              <tr key={book.book_id}>
                <td>{book.bookName}</td>
                <td>${book.price}</td>
                <td>
                  <button onClick={() => handleUpdate(book.book_id)} className="btn btn-warning btn-sm">Update</button>
                  <button onClick={() => handleDelete(book.book_id)} className="btn btn-danger btn-sm ml-2">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
