import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../Components/CSS/UserDashboard.css';
import greatgatsby from '../images/TheGreatGatsby.jpg';
import killm from '../images/To Kill a Mockingbird.jpg'; 
import war from '../images/The Art of War.jpg';
import attitude from '../images/Attitude-is-Everything.jpg';
import harry from '../images/harry.jpg';
import hobbit from '../images/hobbit.jpg';

const bookImageMap = {
  'The Great Gatsby': greatgatsby,
  'To Kill a Mockingbird': killm,
  'The Art of War': war,
  'Attitude is Everything': attitude,
  'Harry Potter and the Sorcerer\'s Stone': harry,
  'The Hobbit': hobbit,
};

function UserDashboard() {
  const [currentSection, setCurrentSection] = useState('home');
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (currentSection === 'books') {
      axios.get('http://localhost:8080/books')
        .then(response => {
          setBooks(response.data);
          setLoading(false);
        })
        .catch(error => {
          setError('Error fetching books. Please try again later.');
          setLoading(false);
        });
    }
  }, [currentSection]);

  const handleShowSection = (section) => {
    setCurrentSection(section);
  };

  return (
    <div className="dashboard-container">
      <nav className="navbar">
        <div className="navbar-left">
          <span className="navbar-brand">BookBliss</span>
        </div>
        <div className="navbar-right">
          <ul>
            <li><a href="#" onClick={() => handleShowSection('home')}>Home</a></li>
            <li><a href="#" onClick={() => handleShowSection('books')}>Books</a></li>
            <li><a href="#" onClick={() => handleShowSection('cart')}>Cart</a></li>
          </ul>
        </div>
      </nav>
      <div className="content">
        {currentSection === 'books' && (
          <div className="books-page">
            <h1>Books Collection</h1>
            {loading && <p>Loading...</p>}
            {error && <p className="error-message">{error}</p>}
            {!loading && !error && (
              <div className="books-list">
                {books.length > 0 ? (
                  books.map((book) => (
                    <div key={book.id} className="book-card">
                      <img
                        src={bookImageMap[book.bookName] || '/images/default-book.avif'}
                        alt={book.bookName}
                        className="book-image"
                      />
                      <div className="book-details">
                        <h2 className="book-title">{book.bookName}</h2>
                        <p className="book-author">by {book.authorId}</p>
                        <p className="book-price">${book.price}</p>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No books available.</p>
                )}
              </div>
            )}
          </div>
        )}
        {currentSection === 'home' && <div>Home Section Content</div>}
        {currentSection === 'cart' && <div>Cart Section Content</div>}
      </div>
    </div>
  );
}

export default UserDashboard;
