import React, { useState } from 'react';
import axios from 'axios';
import '../Components/CSS/Login.css'; // Import the CSS file

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        try {
            const response = await axios.post('http://localhost:8080/login', null, {
                params: {
                    email,
                    password
                }
            });
            
            const { redirectUrl } = response.data;
            if (redirectUrl) {
                // Redirect to the appropriate URL
                window.location.href = redirectUrl;
            }
        } catch (err) {
            if (err.response && err.response.status === 401) {
                setError('Invalid email or password.');
            } else {
                setError('An error occurred. Please try again.');
            }
        }
    };

    return (
        <section className="login-form-container" id="login">
            <div className="login-form-container">
                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        className="box"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        className="box"
                        placeholder="Enter your password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <input type="submit" value="Login Now" className="btn" />
                    <input type="checkbox" id="remember" />
                    <label htmlFor="remember">Remember me</label>
                    <p>Forgot password? <a href="login.html">Click here</a></p>
                    <p>Don't have an account? <a href="signup.html">Register now</a></p>
                    {error && <p className="error">{error}</p>}
                </form>
            </div>
        </section>
    );
}

export default Login;
