import React, {useState} from 'react'
import {Link, useNavigate} from 'react-router-dom';
// import 'bootstrap/dist/css/bootstrap.min.css';
import '../Components/CSS/Dashboard.css'; // Ensure you have CSS file for styles


function CustomerDashboard() {

    const navigate = useNavigate();
    const handleLogout = () => {
        // Perform any necessary logout operations, e.g., clearing tokens
        localStorage.removeItem('token'); // Example: removing a token from local storage
        // Redirect to login page
        navigate('/');
      };

    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);

    // change nav color when scrolling
    const [color, setColor] = useState(false);
    const changeColor = () => {
        if(window.scrollY >= 90){
            setColor(true)
        }else{
            setColor(false)
        }
    }
    window.addEventListener('scroll', changeColor)

    // close menu on click
    const closeMenu = () => setClick(false);
  return (
<nav className={`container-fluid navbar navbar-expand-lg sticky-top content ${color ? 'navbar-dark header-bg' : 'navbar-light'}`} style={{ backgroundColor: '#2d2e30' }}>
    <div className="container-fluid">
        <Link className="navbar-brand" to="/" style={{fontSize : "1.5rem", color : "orange"}}>BookBliss</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                    <Link className="nav-link" aria-current="page" to="/">Home</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/About">About</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/Book">Books</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/Cart">Cart</Link> {/* Add Cart link */}
                </li>
            </ul>
            <form class="d-flex">
        <button class="btn btn-outline-success text-bg-danger p-3" type="submit" onClick={handleLogout}>Logout</button>
      </form>
        </div>
    </div>
</nav>
  );
}

export default CustomerDashboard;
