import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import bookImage from '../images/Books.jpg'; // Path to the image in the `images` folder

function Home() {
  return (
    <Container className="mt-4">
      <Row>
        <Col md={6}>
          <h1>Welcome to the Book Store</h1>
          <p>Explore a wide range of books and find your next read!</p>
        </Col>
        <Col md={6}>
          <img 
            src={bookImage}
            alt="Books"
            className="img-fluid"
            style={{ maxHeight: '300px', objectFit: 'cover' }}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
