import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../Components/CSS/AdminDashboard.css'; // Ensure you have a CSS file for styles

function AdminDashboard() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:8080/books');
        setBooks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching books.');
        setLoading(false);
        console.error('Error fetching books:', err);
      }
    };

    fetchBooks();
  }, []);

  const handleDeleteBook = async (bookId) => {
    try {
      await axios.delete(`http://localhost:8080/books/${bookId}`);
      setBooks(books.filter(book => book.id !== bookId));
    } catch (err) {
      setError('Error deleting book.');
      console.error('Error deleting book:', err);
    }
  };

  const handleUpdateBook = (bookId) => {
    navigate(`/update-book/${bookId}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token or other auth-related data
    navigate('/'); // Redirect to the login page
  };

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <h2>Admin Dashboard</h2>
        <button onClick={handleLogout} className="btn btn-danger">Logout</button>
      </div>
      <div className="content">
        <h1>Books Collection</h1>
        {error && <p className="error-message">{error}</p>}
        {loading && <p>Loading...</p>}
        {!loading && (
          <div className="book-container">
            {books.length > 0 ? (
              books.map(book => (
                <div key={book.id} className="book-card">
                  <img
                    src='https://img.freepik.com/premium-photo/landscape-open-book-with-cover-mockup-mockup_641503-157256.jpg' // Default image URL
                    alt={book.bookName}
                    className="book-image"
                  />
                  <div className="book-details">
                    <h3>{book.bookName}</h3>
                    <p>by {book.authorId}</p>
                    <p>${book.price}</p>
                    <button onClick={() => handleUpdateBook(book.id)} className="btn btn-warning">Update</button>
                    <button onClick={() => handleDeleteBook(book.id)} className="btn btn-danger">Delete</button>
                  </div>
                </div>
              ))
            ) : (
              <p>No books available.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
