import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Components/CSS/Dashboard.css';
import '../Components/CSS/Book.css'; // Ensure the book styles are included

function CustomerDashboard() {
  const navigate = useNavigate();
  const [showBooks, setShowBooks] = useState(false);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const [color, setColor] = useState(false);
  const changeColor = () => {
    if (window.scrollY >= 90) {
      setColor(true);
    } else {
      setColor(false);
    }
  };
  window.addEventListener('scroll', changeColor);

  const closeMenu = () => setClick(false);

  const fetchBooks = () => {
    setLoading(true);
    axios.get('http://localhost:8080/books')
      .then(response => {
        setBooks(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Error fetching books. Please try again later.');
        setLoading(false);
        console.error('Error fetching books:', error);
      });
  };

  const handleShowBooks = () => {
    setShowBooks(!showBooks);
    if (!showBooks) {
      fetchBooks();
    }
  };

  return (
    <div>
      <nav className={`container-fluid navbar navbar-expand-lg sticky-top content ${color ? 'navbar-dark header-bg' : 'navbar-light'}`} style={{ backgroundColor: '#2d2e30' }}>
        <div className="container-fluid">
          <Link className="navbar-brand" to="/" style={{ fontSize: '1.5rem', color: 'orange' }}>BookBliss</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link" aria-current="page" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <button className="nav-link btn btn-link" onClick={handleShowBooks}>Books</button>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/About">About</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Cart">Cart</Link>
              </li>
            </ul>
            <form className="d-flex">
              <button className="btn btn-outline-success text-bg-danger p-3" type="submit" onClick={handleLogout}>Logout</button>
            </form>
          </div>
        </div>
      </nav>

      {/* Conditional rendering of books */}
      {showBooks && (
        <div className="books-page">
          <h1>Books Collection</h1>
          {loading && <p>Loading...</p>}
          {error && <p className="error-message">{error}</p>}
          {!loading && !error && (
            <div className="books-list">
              {books.length > 0 ? (
                books.map((book) => (
                  <div key={book.id} className="book-card">
                    <img
                      src={bookImageMap[book.bookName] || '/images/default-book.avif'}
                      alt={book.bookName}
                      className="book-image"
                    />
                    <div className="book-details">
                      <h2 className="book-title">{book.bookName}</h2>
                      <p className="book-author">by {book.aname}</p>
                      <p className="book-price">${book.price}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p>No books available.</p>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default CustomerDashboard;
