import React from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../Components/CSS/Dashboard.css'; // Ensure you have CSS file for styles

function CustomerDashboard() {

    const handleLogout = () => {
        // Perform any necessary logout operations, e.g., clearing tokens
        localStorage.removeItem('token'); // Example: removing a token from local storage
    
        // Redirect to login page
        navigate('/login');
      };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h2>BookBliss</h2>
        <ul>
          <li><Link to="/home">Home</Link></li>
          <li><Link to="/books">Books</Link></li>
          <li><Link to="/cart">Cart</Link></li>
        </ul>
        <button onClick={handleLogout} className="btn btn-danger logout-btn">
          Logout
        </button>
      </div>
      <div className="content">
        {/* Content of the dashboard */}
        <h1>Welcome to your BookBliss!</h1>
        {/* Add more content here as needed */}
      </div>
    </div>
  );
}

export default CustomerDashboard;
