import React, { useState } from 'react';
import axios from 'axios';
import '../Components/CSS/SearchBooksByAuthor.css';

function SearchBooksByAuthor() {
  const [authorName, setAuthorName] = useState('');
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.get(`http://localhost:8080/book/author/${authorName}`)
      .then(response => {
        setBooks(response.data);
        setError(null);
      })
      .catch(error => {
        setError('No books found for this author.');
        setBooks([]);
        console.error('Error searching books by author:', error);
      });
  };

  return (
    <div>
      <h2>Search Books by Author</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Author Name:
          <input
            type="text"
            value={authorName}
            onChange={(e) => setAuthorName(e.target.value)}
            required
          />
        </label>
        <button type="submit">Search</button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {books.length > 0 && (
        <div className="books-list">
          {books.map((book) => (
            <div key={book.book_id} className="book-card">
              <img
                src='https://img.freepik.com/premium-photo/landscape-open-book-with-cover-mockup-mockup_641503-157256.jpg'
                alt={book.bookName}
                className="book-image"
              />
              <div className="book-details">
                <h3 className="book-title">{book.bookName}</h3>
                <p className="book-author">by {book.author.aname}</p>
                <p className="book-price">${book.price}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchBooksByAuthor;
