import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Components/CSS/BookDetails.css'; // Ensure you have CSS file for styles

function BookDetails({ bookId, onClose }) {
  const [book, setBook] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/books/${bookId}`);
        setBook(response.data);
      } catch (err) {
        setError('Error fetching book details.');
        console.error('Error fetching book details:', err);
      }
    };

    fetchBookDetails();
  }, [bookId]);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!book) {
    return <p>Loading...</p>;
  }

  return (
    <div className="book-details">
      <h3>Book Details</h3>
      <p><strong>Title:</strong> {book.bookName}</p>
      <p><strong>Author:</strong> {book.aname}</p>
      <p><strong>Price:</strong> ${book.price}</p>
      <p><strong>Quantity:</strong> {book.quantity}</p>
      <button onClick={onClose} className="btn btn-secondary">Close</button>
    </div>
  );
}

export default BookDetails;
