//   <div class="container-fluid">
//     <a class="navbar-brand" href="#">Navbar</a>
//     <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
//       <span class="navbar-toggler-icon"></span>
//     </button>
//     <div class="collapse navbar-collapse" id="navbarSupportedContent">
//       <ul class="navbar-nav me-auto mb-2 mb-lg-0">
//         <li class="nav-item">
//           <a class="nav-link" aria-current="page" href="#">Home</a>
//         </li>
//         <li class="nav-item">
//           <a class="nav-link" href="#">Book</a>
//         </li>
//       </ul>
//       <form class="d-flex" role="search">
//         <button class="btn btn-outline-success" type="submit">Logout</button>
//       </form>
//     </div>
//   </div>
// </nav>
<nav className={`container-fluid navbar navbar-expand-lg sticky-top content ${color ? 'navbar-dark header-bg' : 'navbar-light'}`} style={{ backgroundColor: '#2d2e30' }}>
    <div className="container-fluid">
        <Link className="navbar-brand bookbliss-brand" to="/">BookBliss</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                    <Link className="nav-link" aria-current="page" to="/">Home</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/About">About</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/Books">Books</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/Cart">Cart</Link> {/* Add Cart link */}
                </li>
            </ul>
            <form class="d-flex">
        <button class="btn btn-outline-success text-bg-danger p-3" type="submit" onClick={handleLogout}>Logout</button>
      </form>
        </div>
    </div>
</nav>
  );
}

export default CustomerDashboard;
