// src/Components/PaymentDetails.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PaymentDetails.css'; // Optional: CSS file for styling

function PaymentDetails() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const response = await axios.get('http://localhost:8080/payments'); // Adjust URL if needed
        setPayments(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching payments.');
        setLoading(false);
        console.error('Error fetching payments:', err);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="payment-details">
      <h2>Payment Details</h2>
      {loading && <p>Loading...</p>}
      {error && <p className="error-message">{error}</p>}
      {!loading && !error && (
        <table className="payment-table">
          <thead>
            <tr>
              <th>Payment ID</th>
              <th>Order ID</th>
              <th>User ID</th>
              <th>Payment Status</th>
              <th>Payment Mode</th>
            </tr>
          </thead>
          <tbody>
            {payments.length > 0 ? (
              payments.map(payment => (
                <tr key={payment.paymentId}>
                  <td>{payment.paymentId}</td>
                  <td>{payment.order ? payment.order.orderId : 'N/A'}</td>
                  <td>{payment.user ? payment.user.userId : 'N/A'}</td>
                  <td>{payment.paymentStatus}</td>
                  <td>{payment.mode}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">No payments found.</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default PaymentDetails;
