import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UpdateBookForm from './UpdateBookForm'; // Import the UpdateBookForm component
import '../Components/CSS/AdminDashboard.css'; // Ensure you have CSS file for styles

function AdminDashboard() {
  const [showBooks, setShowBooks] = useState(false);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [bookId, setBookId] = useState('');
  const [bookDetails, setBookDetails] = useState(null);

  useEffect(() => {
    if (showBooks) {
      axios.get('http://localhost:8080/books') // Replace with your API URL
        .then(response => {
          setBooks(response.data);
          setLoading(false);
        })
        .catch(error => {
          setError('Error fetching books. Please try again later.');
          setLoading(false);
          console.error('Error fetching books:', error);
        });
    }
  }, [showBooks]);

  const handleUpdateBook = (book) => {
    setSelectedBook(book);
  };

  const handleCloseUpdateForm = () => {
    setSelectedBook(null);
  };

  const handleUpdateSuccess = () => {
    // Refresh the book list after successful update
    axios.get('http://localhost:8080/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error refreshing book list:', error));
    handleCloseUpdateForm();
  };

  const handleGetBookById = () => {
    axios.get(`http://localhost:8080/books/${bookId}`)
      .then(response => setBookDetails(response.data))
      .catch(error => {
        setError('Error fetching book details.');
        console.error('Error fetching book details:', error);
      });
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Example: removing a token from local storage
    window.location.href = '/'; // Redirect to the home page
  };

  return (
    <div className="admin-dashboard-container">
      <div className="sidebar">
        <h2>Admin Dashboard</h2>
        <ul>
          <li><a href="#view-books" onClick={() => setShowBooks(!showBooks)}>{showBooks ? 'Hide Books' : 'View Books'}</a></li>
          <li><a href="#get-book" onClick={() => setBookDetails(null)}>Get Book by ID</a></li>
          <li><a href="#order-details">Get Order Details</a></li>
          <li><a href="#payment-details">Payment Details</a></li>
          <li><button onClick={handleLogout} className="logout-button">Logout</button></li>
        </ul>
      </div>
      <div className="content">
        {showBooks && (
          <div id="view-books" className="books-list">
            <h2>Books Management</h2>
            {loading && <p>Loading...</p>}
            {error && <p className="error-message">{error}</p>}
            {!loading && !error && (
              <div className="book-container">
                {books.length > 0 ? (
                  books.map((book) => (
                    <div key={book.id} className="book-card">
                      <img
                        src='https://img.freepik.com/premium-photo/landscape-open-book-with-cover-mockup-mockup_641503-157256.jpg' // Default image URL
                        alt={book.bookName}
                        className="book-image"
                      />
                      <div className="book-details">
                        <h3 className="book-title">{book.bookName}</h3>
                        <p className="book-author">by {book.aname}</p>
                        <p className="book-price">${book.price}</p>
                        <button onClick={() => handleUpdateBook(book)}>Update</button>
                        <button className="btn btn-danger btn-sm ml-2">Delete</button>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No books available.</p>
                )}
              </div>
            )}
          </div>
        )}
        <div id="get-book" className="book-id-section">
          <h2>Get Book by ID</h2>
          <form className="book-id-form" onSubmit={(e) => { e.preventDefault(); handleGetBookById(); }}>
            <div className="form-group">
              <label htmlFor="bookId">Enter Book ID:</label>
              <input
                type="number"
                id="bookId"
                value={bookId}
                onChange={(e) => setBookId(e.target.value)}
                placeholder="Book ID"
                required
              />
            </div>
            <button type="submit" className="submit-button">Submit</button>
          </form>
          {bookDetails && (
            <div className="book-details">
              <h3>{bookDetails.bookName}</h3>
              <p><strong>Author:</strong> {bookDetails.aname}</p>
              <p><strong>Price:</strong> ${bookDetails.price}</p>
              <p><strong>Quantity:</strong> {bookDetails.quantity}</p>
            </div>
          )}
          {error && <p className="error-message">{error}</p>}
        </div>
        {selectedBook && (
          <UpdateBookForm
            bookId={selectedBook.id}
            onClose={handleCloseUpdateForm}
            onUpdate={handleUpdateSuccess}
          />
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
