import React from 'react';
// import './style.css'; // Make sure this path is correct
// import './script.js'; // Note: JavaScript files are generally not imported this way in React

function Login() {
  return (
    <section className="login-form-container" id="login">
      {/* login form container */}
      <div className="login-form-container">
        <form action="">
          <input type="email" className="box" placeholder="enter your email" />
          <input type="password" className="box" placeholder="enter your password" />
          <input type="submit" value="login now" className="btn" />
          <input type="checkbox" id="remember" />
          <label htmlFor="remember">remember me</label>
          <p>
            forget password? <a href="Login.js">click here</a>
          </p>
          <p>
            don't have an account? <a href="Signup.js">register now</a>
          </p>
        </form>
      </div>
    </section>
  );
}

export default Login;
